INSERT INTO sort (id, name, description) VALUES (1, 'Chardonnay', 'Chardonnay is a white wine grape variety that is widely planted across the world. It is known for its versatility, ranging from crisp and unoaked styles to rich and buttery expressions.');
INSERT INTO sort (id, name, description) VALUES (2, 'Merlot', 'Merlot is a red wine grape variety known for its softness, medium body, and ripe fruit flavors. It often exhibits notes of blackberries, plums, and chocolate, with a smooth and velvety texture.');
INSERT INTO sort (id, name, description) VALUES (3, 'Cabernet Sauvignon', 'Cabernet Sauvignon is a popular red wine grape variety with bold flavors of blackcurrant, dark cherry, and cedar. It is characterized by its full body, firm tannins, and aging potential.');
INSERT INTO sort (id, name, description) VALUES (4, 'Pinot Noir', 'Pinot Noir is a red wine grape variety renowned for its delicate and complex nature. It typically showcases flavors of red berries, cherries, and earthy notes. Pinot Noir wines can vary in style, ranging from light and fruity to rich and structured.');
INSERT INTO sort (id, name, description) VALUES (5, 'Sauvignon Blanc', 'Sauvignon Blanc is a white wine grape variety known for its vibrant acidity and refreshing character. It exhibits flavors of citrus fruits, green apple, gooseberry, and sometimes grassy or herbal notes.');
INSERT INTO sort (id, name, description) VALUES (6, 'Syrah', 'Syrah (also known as Shiraz) is a red wine grape variety that produces full-bodied wines with intense flavors of blackberries, black pepper, and spices. It often has a rich and velvety texture.');
